# .NET 4.5 System.IO.Compression.FileSystem.dll
$COMPRESSION_DLL_BASE64 = "";
$LOG4J2_PACKAGE="org/apache/logging/log4j/core"
$JNDI_CLASS_NAME="JndiManager.class"
$LOG4J2_LOGGER_CLASS="${LOG4J2_PACKAGE}/Logger.class"
$JNDI_MANAGER_CLASS="${LOG4J2_PACKAGE}/net/$JNDI_CLASS_NAME"
 
$WINDOWS_PATH_LOGGER_CLASS = $LOG4J2_LOGGER_CLASS -replace "/", "\";
 
$PWD = $(pwd).path
$TMP_JNDI_CLASS_FILE="$PWD\$JNDI_CLASS_NAME"
$COMPRESSION_DLL = "$PWD\System.IO.Compression.FileSystem.dll"
$COMPRESSION_DLL_BYTES = [System.Convert]::FromBase64String($COMPRESSION_DLL_BASE64)
[System.IO.File]::WriteAllBytes($COMPRESSION_DLL, $COMPRESSION_DLL_BYTES)
 
if (-not (Test-Path $COMPRESSION_DLL)) {
    Write-Host "$COMPRESSION_DLL was not available exiting..."
    exit 1
}
 
Add-Type -Path "$COMPRESSION_DLL"
 
ForEach ($Drive in (Get-PSDrive -PSProvider Microsoft.PowerShell.Core\FileSystem)) {
    Write-Host "Scanning for zip,jar,ear,war and class files in $($Drive.Root)...";
    ForEach ($file in (Get-ChildItem -Path $Drive.Root -Recurse -Force -ErrorAction SilentlyContinue | Where-Object { $_.Name -match '^.+\.(zip|[wej]ar|class)$'})) {
        Try {
            If ($file.Name -match '^.+\.(zip|[wej]ar)$') {
                $zip = [System.IO.Compression.ZipFile]::OpenRead($file.FullName);
                $log4j2_archive = $false;
                $vulnerable = $true;
                $logger_entry = $zip.GetEntry("$LOG4J2_LOGGER_CLASS");
                $jndimanager_entry= $zip.GetEntry("$JNDI_MANAGER_CLASS");
                if ($logger_entry) {
                    $log4j2_archive = $true
                    if ($jndimanager_entry) {
                        [System.IO.Compression.ZipFileExtensions]::ExtractToFile($jndimanager_entry, "$TMP_JNDI_CLASS_FILE", $true)
                        if (cat $TMP_JNDI_CLASS_FILE | Select-String 'log4j2') {
                            $vulnerable = $false
                        }
                        rm $TMP_JNDI_CLASS_FILE
                    }
                }
                if ($log4j2_archive -and $vulnerable) {
                    Write-Host "[!] Vulnerable archive found: $($file.FullName)"
                }
                $zip.Dispose();
            }
            if ($file.FullName -match '^.+\\Logger.class$') {
                $root_class_dir = "";
                if ($file.FullName -like "*$WINDOWS_PATH_LOGGER_CLASS") {
                   $vulnerable = $true
                   $jndi_manager_class_file = $file.FullName -replace "Logger", "JndiManager";
                   if (Test-Path $jndi_manager_class_file) {
                       if (cat $jndi_manager_class_file | Select-String 'log4j2') {
                            $vulnerable = $false
                        }
                   }
                   if ($vulnerable) {
                       Write-Host "[!] Vulnerable class found: $($file.FullName)"
                   }
                }
            }
        } Catch [Exception] {
            Write-Host "Skipping $($file.FullName) as could not open..." -ForegroundColor Gray
        }
    }
} 
 
rm $COMPRESSION_DLL
